con:config.script:
def accessToken = context.expand('${Given i request an access token#Response#$.access_token}')
def tokenType = context.expand( '${Given i request an access token#Response#$[\'token_type\']}' )
testRunner.testCase.setPropertyValue("ACCESS_TOKEN", accessToken)
testRunner.testCase.setPropertyValue("THEDATE", new Date().format('dd/MM/YY'))
return tokenType + ' ' + accessToken

//convert value: NS == No, S == Yes
def smoker = context.expand( '${When i input a user\'s information#Smoker}' ) as String
testRunner.testCase.setPropertyValue("SMOKER", (smoker == 'NS') ?  "No" : "Yes")
//convert value: 3+years == true, rest == false
def selfEmployed = context.expand( '${When i input a user\'s information#Self Employed}' ) as String
testRunner.testCase.setPropertyValue("SEGT3", (selfEmployed == '3+ years') ? "true" : "false")
//convert value: M == Monthly, W == Weekly, F == Fortnightly, Y == Annual
def premiumFrequency = context.expand( '${When i input a user\'s information#Premium Frequency}' ) as String
def pF = ""
switch(premiumFrequency)
{
	case "W":
		pF = "Weekly";
		break;
	case "M":
		pF = "Monthly";
		break;
	case "F":
		pF = "Fortnightly";
		break;
	case "Y":
		pF = "Yearly";
		break;
}
testRunner.testCase.setPropertyValue("FREQUENCY", pF)
//convert value: according to Occupational classes, only 5 is Unemployed
def employedStatus = context.expand( '${When i input a user\'s information#Occupation Class}' ) as Integer
testRunner.testCase.setPropertyValue("EMPLOYEDSTATUS", (employedStatus == 5) ? "Unemployed" : "Employed")
//convert value: M == Male, F == Female
def gender = context.expand( '${When i input a user\'s information#Sex}' ) as String
testRunner.testCase.setPropertyValue("GENDER", (gender == 'M') ? "Male" : "Female")
//convert value: M == Male, F == Female
testRunner.testCase.setPropertyValue("LIFEINDEX", (context.expand( '${When i input a user\'s information#Life - Indexing}' ) == 'Y') ? "Yes" : "No")
//convert value: 1 means 100
def lifeLoading = context.expand( '${When i input a user\'s information#Life - Loading}' ) as Double
testRunner.testCase.setPropertyValue("LOAD", (lifeLoading * 100).toString() )
//convert Premium Opt
//convert Review Term
String _opt = ""
String _term = ""
def lifePremiumOptions = context.expand( '${When i input a user\'s information#Life - Premium Options}' ) as String
switch(lifePremiumOptions)
{
	case ~/.*Term1-RFA.*/:
		_opt = "Rate for Age"
		_term = "1"
		break
	case~/.*Term10-.*/:
		_opt = "10 Year"
		_term = "10"
		break
	case~/.*Term65-.*/:
		_opt = "To Age 65"
		_term = "65"
		break
	case~/.*Term80-.*/:
		_opt = "To Age 80"
		_term = "80"
		break
	default:
		_opt = "No Matched Options"
		_term = "No Matched Term"
}
testRunner.testCase.setPropertyValue("PRE_OPT", _opt )
testRunner.testCase.setPropertyValue("REVIEW_TERM", _term )

